import React, {useEffect, useState} from 'react'
import '../Cards/Card.css'

/**
* @author
* @function Cards
**/

const Cards = (props) => {
const [bar, setBar] = useState(false)

useEffect(()=>{
const type = props.type
    if(type === 'bar'){
        setBar(true)
    }
},[])
  return(
    <div className='card'>
        {bar?'bar true':''}
    </div>
   )

 }

export default Cards
