import React, {useEffect, useState} from 'react'
import '../Cards/Card.css'

/**
* @author
* @function Cards
**/

const Cards = (props) => {
const [bar, setBar] = useState(false)
const [occupancy, setOccupancy] = useState(false)
const [dailySpend, setDailySpend] = useState(false)
const [payments, setPayments] = useState(false)
const [liveVisitors, setLiveVisitors] = useState(false)

useEffect(()=>{
const type = props.type
    if(type === 'bar'){
        setBar(true)
    } else if(type === 'occupancy'){
        setOccupancy(true)
    }else if(type === 'dailySpend'){
        setDailySpend(true)
    }else if(type === 'payments'){
        setPayments(true)
    }else if(type === 'liveVisitors'){
        setLiveVisitors(true)
    }

},[])
  return(
    <div className={liveVisitors?'card_long':'card'}>
        {bar?
            <div>
                <div className='titles'><h2>Peak times</h2></div>
            </div>
        :''}
        {occupancy? 
        <div>
            <div className='titles'><h2>Occupancy</h2></div>
        </div>:
        ''}
        {dailySpend? 
        <div>
            <div className='titles'><h2>Daily Spend</h2></div>
        </div>:
        ''}
        {payments? 
        <div>
            <div className='titles'><h2>Payments</h2></div>
        </div>:
        ''}
        {liveVisitors? 
            <div>
                <div className='titles'>
                    <h2>Live Visitors</h2>
                </div>
               <div>


               </div>




            </div>:
            ''}
        </div>
   )

 }

export default Cards
